
function popup(e,t=""){const n=document.createElement("div");n.innerHTML=e,n.className="popup",n.id=popup_reset("popup-temporary"),document.getElementsByTagName("body")[0].appendChild(n),setTimeout((function(){popup_fire(n.id,10,Array.from({length:100},((e,t)=>.01*t)).reverse())}),300)}function popup_reset(e){return null!=document.getElementById(e)?popup_reset(e+"-altenative"):e}function popup_fire(e,t,n){if(null!=document.getElementById(e))if(n.length){document.getElementById(e).style.opacity=n.shift(),setTimeout((function(){popup_fire(e,t,n)}),t)}else document.getElementById(e).remove()}
